import React, { useContext } from "react";
import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import { Link, useHistory } from "react-router-dom";
import Button from "react-bootstrap/Button";
import AuthContext from "../../store/auth-context";


export default function MainHeader() {
  const authctx=useContext(AuthContext);
  const login=authctx.isLoggenIn;


  const history=useHistory();
  const logouthandler=()=>{
    authctx.logout();
    history.replace('/auth');
  }

  return (
    <Navbar bg="dark" expand="lg" variant="dark">
        <Container>
          <Navbar.Brand href="#home">E-Comm Store</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              <Link to="/">
                <Button variant="outline-light" size="sm" className="mx-2">
                  Home
                </Button>
              </Link>

              <Link to="/store">
                <Button variant="outline-light" size="sm" className="mx-2">
                  Store
                </Button>
              </Link> 
              <Link to="/about">
                <Button variant="outline-light" size="sm" className="mx-2">
                  About
                </Button>
              </Link>
              <Link to="/contact">
                <Button variant="outline-light" size="sm" className="mx-2">
                  Contact US
                </Button>
              </Link>
              { !login && 
              <Link to="/auth">
                <Button variant="outline-light" size="sm" className="mx-2">
                  Login
                </Button>
              </Link> }
              {login && <Button variant="outline-light" size="sm" className="mx-2" onClick={logouthandler}>
                  Log out
                </Button>}

            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
  )
}
